mapper.select:
SELECT COUNT(S_NUM) CNT
FROM FAV_STUDY
WHERE ID = #{id} AND S_NUM = #{myFavSNum}

SELECT S_NUM, STUDY_NAME, LOC, CATEGORY, IMG, RECRUITMENT, TO_CHAR(INPUT_DATE, 'YYYY/MM/DD') INPUT_DATE, NICK, USER_IMG FROM (SELECT S.S_NUM, S.STUDY_NAME, S.LOC, S.CATEGORY, S.IMG, S.RECRUITMENT, S.INPUT_DATE, U.NICK, U.IMG USER_IMG, ROW_NUMBER() OVER( ORDER BY S.INPUT_DATE DESC) R_NUM FROM FAV_STUDY FS, STUDY S, USER_TABLE U WHERE ( FS.S_NUM = S.S_NUM ) AND ( S.ID=U.ID ) AND FS.ID = #{ my_id }
AND LOC = '${fav_loc_select}'
AND CATEGORY = '${fav_kind_select}'
AND LOC = '${fav_loc_select}' and CATEGORY = '${fav_kind_select}'
) ORDER BY R_NUM ASC
