mapper.select:
SELECT COUNT(*) cnt
FROM fav_study
WHERE s_num=#{ s_num }

SELECT COUNT(*) cnt
FROM member
WHERE s_num=#{ s_num }

SELECT COUNT(*) cnt
FROM member
WHERE s_num=#{ s_num } AND id=#{ id }

SELECT COUNT(*) cnt
FROM join
WHERE s_num=#{ s_num } AND id=#{ id }

SELECT COUNT(*) cnt
FROM study
WHERE s_num = #{ s_num } AND id=#{ id }

SELECT S_COMMENT, ID,TO_CHAR(INPUT_DATE, 'yyyy/mm/dd') INPUT_DATE, IMG
FROM (SELECT SC.S_COMMENT, SC.ID, UT.IMG, SC.INPUT_DATE, ROW_NUMBER() OVER( ORDER BY SC.INPUT_DATE DESC) R_NUM
		FROM STUDY STD, S_COMMENT SC, USER_TABLE UT
		WHERE STD.S_NUM=SC.S_NUM AND UT.ID = SC.ID AND SC.S_NUM= #{sNum}
		)
ORDER BY R_NUM ASC

SELECT UT.IMG leaderImg, UT.NICK nick, STD.STUDY_NAME studyName
FROM USER_TABLE UT, STUDY STD
WHERE UT.ID = STD.ID AND STD.S_NUM = #{SNUM}

SELECT IMG
FROM USER_TABLE
WHERE ID = #{id}

SELECT nick
FROM user_table
WHERE id=#{ id }
