mapper.select:
SELECT COUNT(*) cnt
FROM study
WHERE id=#{ id } AND s_num=#{ s_num }

select count(*) cnt
from sn_comment
where sn_num=#{ sn_num }

mapper.update:
UPDATE homework
SET finish_flag='Y'
WHERE id=#{ id } AND sn_num = #{ sn_num }

mapper.select:
SELECT study_name, recruitment
FROM study
WHERE s_num = #{ s_num }

SELECT id
FROM user_table
WHERE nick=#{ nick }

mapper.update:
UPDATE study
SET recruitment=#{ status }
WHERE s_num=#{ s_num }

UPDATE study_notice
SET subject=#{ subject }, meeting_info=#{ meeting_info }, content=#{ content }, addr=#{ addr }
WHERE sn_num = #{ sn_num }
