mapper.select:
select sn.subject, to_char(sn.content) , sn.meeting_info, sn.addr, to_char(sn.input_date,'yyyy-MM-dd') input_date, ut.nick, ut.img
from study_notice sn, study s, user_table ut
where  s.id=ut.id and s.s_num=sn.s_num and sn.sn_num=#{snNum}

select hw.id, hw.workload, hw.finish_flag, ut.nick
from homework hw, user_table ut
where hw.id = ut.id and sn_num=#{snNum}

select sc.sn_comment, sc.input_date, ut.nick
from sn_comment sc, user_table ut
where sc.id= ut.id and sn_num=#{snNum}

mapper.update:
UPDATE study
SET recruitment=#{ status }
WHERE s_num=#{ sNum }

mapper.select:
SELECT id
FROM user_table
WHERE nick=#{ nick }
